	@Override
	public void beforeClickOn(WebElement element, WebDriver driver) {
		String txt = element.getText();
		txt = StringUtils.trim(txt);
//		System.out.println("Driver is going to click on: "+ txt);
		new Reporter().report_step("click", "Driver is going to click on: "+ txt, "pass");
	}

